import { useState } from "react";

const tempMovieData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
  },
  {
    imdbID: "tt0133093",
    Title: "The Matrix",
    Year: "1999",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg",
  },
  {
    imdbID: "tt6751668",
    Title: "Parasite",
    Year: "2019",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg",
  },
];

const tempWatchedData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: "tt0088763",
    Title: "Back to the Future",
    Year: "1985",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];

const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);

export default function App() {
  const [movies, setMovies] = useState(tempMovieData);
  return (
    <>
      <NavBar movies={movies} />
      <Main movies={movies} />
    </>
  );
}
function NavBar() {
  return (
    <div className="nav-bar">
      <Logo />
      <Search />
      <NumResult movies={movies} />
    </div>
  );
}
function Logo() {
  return (
    <div className="logo">
      <span role="img">üçø</span>
      <h1>usePopcorn</h1>
    </div>
  );
}
function Search() {
  const [query, setQuery] = useState("");
  return (
    <div className="search">
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      ></input>
    </div>
  );
}
function NumResult({ movies }) {
  return (
    <div className="num-results">
      <p>
        {" "}
        Found <strong> {movies.length}</strong> result
      </p>
    </div>
  );
}
function Main({ movies }) {
  return (
    <div className="main">
      <Listbox movies={movies} />
      <WatchedBox />
    </div>
  );
}
function Listbox({ movies }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button onClick={() => setIsOpen(!isOpen)} className="btn-toggle">
        {isOpen ? "+" : "-"}
      </button>
      {isOpen && <MovieList movies={movies} />}
    </div>
  );
}
function MovieList(movies) {
  return (
    <ul className="list">
      {movies.map((movie) => (
        <Movie movie={movie} />
      ))}
    </ul>
  );
}
function Movie({ movie }) {
  return (
    <li key={movie.imdbID}>
      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <h3>{movie.Title}</h3>
      <div>
        <p>
          <span>üóì</span>
          <span>{movie.Year}</span>
        </p>
      </div>
    </li>
  );
}
function WatchedBox() {
  const [isOpen2, setIsOpen2] = useState(false);
  const [watched, setWatched] = useState(tempWatchedData);
  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen2(!isOpen2)}>
        {isOpen2 ? "+" : "-"}
      </button>
      {isOpen2 && (
        <>
          <WatchedSummary watched={watched} />
          <WatchedMovieList watched={watched} />
        </>
      )}
    </div>
  );
}
function WatchedSummary({ watched }) {
  const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));
  return (
    <div className="summary">
      <h2>Movies you watched</h2>
      <div>
        <p>
          <span>#Ô∏è‚É£</span>
          <span>{watched.length} movies</span>
        </p>
        <p>
          <span>‚≠ê</span>
          <span>{avgImdbRating}</span>
        </p>
        <p>
          <span>‚≠ê</span>
          <span>{avgUserRating}</span>
        </p>
        <p>
          <span>‚åõ</span>
          <span>{avgRuntime} min</span>
        </p>
      </div>
    </div>
  );
}
function WatchedMovieList({ watched }) {
  return (
    <ul>
      {watched.map((movie) => (
        <WatchedMovie movie={movie} watched={watched} />
      ))}
    </ul>
  );
}
function WatchedMovie({ watched }) {
  return (
    <li key={watched.imdbID}>
      <img src={watched.Poster} alt={`${watched.Title} poster`} />
      <h3>{watched.Title}</h3>
      <p>
        <span>‚≠ê</span>
        <span>{watched.imdbRating}</span>
      </p>
      <p>
